import React from "react";
import "./ProjectCard.scss"
import AOS from 'aos';
import { useEffect } from "react";

export default function ProjectCard({title, image, caption, gitLink, webLink}) {
    useEffect(() => {
        AOS.init();
        AOS.refresh();
      }, []);
    return (
        <div className="project-card card mx-auto mb-5 pb-5" style={{width: "100%"}} data-aos="fade-up">
            <img className="card-img-top" src={image} alt={`${title} logo`} style={{height: "18rem"}} />
            <div className="card-body">
                <h5 className="card-title">{title}</h5>
                <p style={{height: "9rem", overflow: "auto"}}>{caption}</p>
                <div className="row text-center">
                    <div className="col-12 mt-3">
                        <a target="__blank" className="mt-3" href={gitLink}><div className="card-button">View Code</div></a>
                    </div>
                    <div className="col-12 mt-3">
                        <a target="__blank" className="mt-3" href={webLink}><div className="card-button">View Project</div></a>
                    </div>
                </div>
            </div>
        </div>
    )
}
